import React, { Component } from 'react';
import { GoogleMap, LoadScript,Marker } from '@react-google-maps/api';

const containerStyle = {
  width: '100%',
  height: '400px'
};

class Map extends React.Component{


 
state =  [{
            "view": {"lat": 33.7872131,"lng":-84.381931},
            "locations":[{"hid":1,"address":"238 Annette St, Toronto, ON M6P 1R1","lat":43.662636,"lng":-79.471069},{"hid":2,"address":"1290 Queen St W, Toronto, ON M6K 1L4","lat":43.642021,"lng":-79.430397},{"hid":3,"address":"1 Austin Terrace, Toronto, ON M5R 1X8","lat":43.67802,"lng":-79.409607},{"hid":4,"address":"290 Bremner Blvd, Toronto, ON M5V 3L9","lat":43.642548,"lng":-79.387161}]
}];

componentDidMount() {
    navigator.geolocation.getCurrentPosition(
        success => {
            this.setState({ view: {lat: success.coords.latitude, lng: success.coords.longitude}});
            console.log(this.state);
        }
    );

    fetch('http://localhost/wwn/index.php/location/list?limit=20', {
    })
    .then(response => {
        return response.json();
    })
    .then(data => {
        console.log("response" + data);
        this.setState({ locations:{data}});
        console.log(this.state);
    })
    .catch(error => console.log('Authorization failed : ' + error.message));
}


  render() {
    return (
        

      <LoadScript
        googleMapsApiKey="AIzaSyB0-ftuMjsHGds4c5TrWgEa7h6ilMfJye8"
      >
            {this.state.markers.map((marker, i) =>{
              return(
                <AnyReactComponent
                  lat={marker.lat}
                  lng={marker.lng}
                  img_src={marker.img_src}
                />

              )
            })} 

        <GoogleMap
          mapContainerStyle={containerStyle}
          center={this.state.view}
          zoom={10}
        >
    
        </GoogleMap>
      </LoadScript>

      
    );
}
}

export default Map
