import React, { Component } from 'react';
import { GoogleMap, LoadScript,Marker } from '@react-google-maps/api';

const containerStyle = {
  width: '100%',
  height: '400px'
};
const [data,setData]=useState([]);



class Map extends React.Component{
    
state = { lat:33.7872131 , lng: -84.381931 };
componentDidMount() {
    navigator.geolocation.getCurrentPosition(
        success => {
            this.setState({ lat: success.coords.latitude, lng: success.coords.longitude });
            console.log(this.state);
        }
    );

    fetch('http://localhost/wwn/index.php/location/list?limit=20', {
    })
    .then(response => response.json())
    .then(function(myJson) {
        console.log(myJson);
        setData(myJson)
      })
    .catch(error => console.log('Authorization failed : ' + error.message));
}


  render() {
    return (

      <LoadScript
        googleMapsApiKey="AIzaSyB0-ftuMjsHGds4c5TrWgEa7h6ilMfJye8"
      >
        <GoogleMap
          mapContainerStyle={containerStyle}
          center={this.state}
          zoom={10}
        >
        
        <Marker position={{ lat: -34.397, lng: 150.644 }}/> 
        </GoogleMap>
      </LoadScript>
    );
}
}

export default Map
