import React, { Component } from 'react';
import { GoogleMap, LoadScript,Marker } from '@react-google-maps/api';

const containerStyle = {
  width: '100%',
  height: '400px'
};

class Map extends React.Component{


 
state =  {
            "view": {"lat": 33.7872131,"lng":-84.381931}

};

componentDidMount() {
    navigator.geolocation.getCurrentPosition(
        success => {
            this.setState({ view: {lat: success.coords.latitude, lng: success.coords.longitude}});
            console.log(this.state);
        }
    );

    fetch('http://localhost/wwn/index.php/location/list?limit=20', {
    })
    .then(response => {
        return response.json();
    })
    .then(markers => {
        //console.log("markers" + data);
        this.setState({markers});
        console.log(this.state);
    })
    .catch(error => console.log('Authorization failed : ' + error.message));
}


  render() {
    return (
        

      <LoadScript
        googleMapsApiKey="AIzaSyB0-ftuMjsHGds4c5TrWgEa7h6ilMfJye8"
      >
        {this.state.markers.map((marker, i) =>{
              return(
                <h1>
                  lat={marker.lat}
                  lng={marker.lng}
                </h1>

              )
            })} 
    

        <GoogleMap
          mapContainerStyle={containerStyle}
          center={this.state.view}
          zoom={10}
        >
    
        </GoogleMap>
      </LoadScript>

      
    );
}
}

export default Map
